import React, { useState } from 'react';

import { Square } from './components/Square';

const Board = () => {
    const [xIsNext, setXIsNext] = useState(true);
    const [squares, setSquares] = useState(Array(9).fill(null));
    const [playerSymbol, setPlayerSymbol] = useState('X');

    const handleClick = (i) => {

        if(calculateWinner(squares) || squares[i]){
          return;
        }

        const nextSquares = [...squares];
        nextSquares[i] = xIsNext ? 'X' : 'O';

        setSquares(nextSquares); 
        setXIsNext(!xIsNext);
    };

    const calculateWinner = (squares) => {
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6]
          ];
          for (let i = 0; i < lines.length; i++){
            const [a, b, c] = lines[i];
            if(squares[a] && squares[a] == squares[b] && squares[a] == squares[c]){
              return squares[a];
            }
          }
          return null;
    };

    const getStatus = () => {
        const winner = calculateWinner(squares);
        if(!squares.includes(null)){
            return 'Draw!';
        } else if (winner) {
            return 'Winner: ' + winner;
        } else {
            return 'Next player: ' + (xIsNext ? 'X' : 'O');
        }

    };

    const resetGame = () => {
        setSquares(Array(9).fill(null));
        setXIsNext(true);
    };

    const handleSymbolChange = (e) => {
        setPlayerSymbol(e.target.value);
        setXIsNext(e.target.value === 'X' ? true : false);
    };

    const renderBoardRow = (rowIndex) => {
        <div className="board-row">
        {[0, 1, 2].map((colIndex) => (
          <Square
            key={rowIndex * 3 + colIndex}
            value={squares[rowIndex * 3 + colIndex]}
            onSquareClick={() => handleClick(rowIndex * 3 + colIndex)}
          />
        ))}
      </div>
    };

    return (
        <>
      <div>
        <label>
          Choose your symbol:{' '}
          <select value={playerSymbol} onChange={handleSymbolChange}>
            <option value="X">X</option>
            <option value="O">O</option>
          </select>
        </label>
      </div>
      <div className="status">{getStatus()}</div>
      {[0, 1, 2].map((rowIndex) => renderBoardRow(rowIndex))}
      <button onClick={resetGame}>Refresh Game</button>
    </>
  );
};

export default Board;
